import { createStore } from 'vuex'

export default createStore({
  state: {
    posts: [], // массив постов
    comments: [], // массив комментариев
    users: [], // массив пользователей
    usersKeyBy: {}, // объект, хранящий пользователей по ID
    commentsGroupBy: {}, // объект, хранящий комментарии, сгруппированные по ID поста
    activePostsTab: true, // активный таб с постами
    activeUsersTab: false, // активный таб с пользователями
    dataAwait: true, // флаг ожидания данных
    nameFilteredUser: "", // имя отфильтрованного пользователя
    activeOneUser: false, // флаг просмотра постов одного пользователя
    page: 1, // текущая страница
    limit: 20, // количество постов, получаемых за один раз
  },
  getters: {
    SET_POSTS(state, posts) {
      state.posts = posts;
    },
    SET_USERS(state, users) {
      state.users = users;
    },
    SET_DATA_AWAIT(state, value) {
      state.dataAwait = value;
    },
  },
  mutations: {
  },
  actions: {
    async fetchPosts({ commit }) {
      commit('SET_DATA_AWAIT', true); // Устанавливаем флаг ожидания

      try {
        // Выполняем запрос к API для получения постов
        const response = await fetch('https://jsonplaceholder.typicode.com/posts');
          const posts = await response.json();
          commit('SET_POSTS', posts);
      } catch (error) {
        console.error('Произошла ошибка:', error);
      } finally {
        commit('SET_DATA_AWAIT', false);
      }
    },
    async fetchUsers({ commit }) {
      const res = await fetch("https://jsonplaceholder.typicode.com/users");
      const users = await res.json();
      commit("SET_USERS", users);
      // ... обработка ошибок и установка флагов
    },
  },
  modules: {
  }
})
