import { createStore } from 'vuex'
import _ from "lodash";

export default createStore({
  state: {
    posts: [],
    comments: [],
    users: [],
    page: 1,
    limit: 20,
    loading: false,
    commentsGroupBy: {},
  },
  mutations: {
    SET_POSTS(state, posts) {
      state.posts = [...posts];
    },
    IS_LOADING(state, value) {
      state.loading = value;
    },
    SET_USERS(state, users) {
      state.users = users;
    },
    SET_COMMENTS(state, comments) {
      state.comments = comments;
      state.commentsGroupBy = _.groupBy(comments, 'postId');
    },
  },
  actions: {
    async fetchPosts({ commit }) {
      commit('IS_LOADING', true)

      try {
        const response = await fetch(`https://jsonplaceholder.typicode.com/posts?_page=${this.page}&_limit=${this.limit}`);
          const posts = await response.json();
          commit('SET_POSTS', posts);
      } catch (error) {
        console.error('Произошла ошибка:', error);
      } finally {
        commit('IS_LOADING', false);
      }
    },
    async fetchUsers({ commit }) {
      commit('IS_LOADING', true); // Устанавливаем флаг ожидания

      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const users = await response.json();
        commit('SET_USERS', users); // Вызываем мутацию для обновления данных
      } catch (error) {
        console.error('Произошла ошибка:', error);
      } finally {
        commit('IS_LOADING', false); // Снимаем флаг ожидания
      }
    },

    async fetchFilteredPosts({ commit }, userId) {
      commit('SET_DATA_AWAIT', true); // Устанавливаем флаг ожидания

      try {
        const response = await fetch(`https://jsonplaceholder.typicode.com/posts?userId=${userId}`);
        const posts = await response.json();
        commit('SET_POSTS', posts); // Вызываем мутацию для обновления данных
      } catch (error) {
        console.error('Произошла ошибка:', error);
      } finally {
        commit('SET_DATA_AWAIT', false); // Снимаем флаг ожидания
      }
    },

    async fetchComments({ commit }) {
      try {
        // Выполняем запрос к API для получения комментариев
        const response = await fetch('https://jsonplaceholder.typicode.com/comments');
        const comments = await response.json();
        commit('SET_COMMENTS', comments); // Вызываем мутацию для обновления данных
      } catch (error) {
        console.error('Произошла ошибка:', error);
      }
    },
  },
  modules: {
  }
})
