import { createStore } from 'vuex'
import _ from "lodash";

export default createStore({
  state: {
    posts: [],
    comments: [],
    users: [],
    page: 1,
    limit: 20,
    loading: true,
  },
  getters: {
  },
  mutations: {
    SET_POSTS(state, posts) {
      state.posts = posts;
    },
    IS_LOADING(state, value) {
      state.dataAwait = value;
    },
  },
  actions: {
    async fetchPosts({ commit }) {
      commit('IS_LOADING', true); // Устанавливаем флаг ожидания

      try {
        // Выполняем запрос к API для получения постов
        const response = await fetch('https://jsonplaceholder.typicode.com/posts');
        if (response.ok) {
          const posts = await response.json();
          commit('SET_POSTS', posts); // Вызываем мутацию для обновления данных
        } else {
          console.error('Ошибка при получении постов с сервера');
        }
      } catch (error) {
        console.error('Произошла ошибка:', error);
      } finally {
        commit('SET_DATA_AWAIT', false); // Снимаем флаг ожидания
      }
    },
    async getPosts() {
      const res = await fetch(
        `https://jsonplaceholder.typicode.com/posts?_page=${this.page}&_limit=${this.limit}`
      );
      const posts = await res.json();
      this.is_data_await = true;

      if (res.ok) {
        this.is_data_await = false;
        this.posts.push(...posts);
      } else {
        console.log("Ошибка получения данных с сервера");
        throw Error;
      }
    },

    async getFilteredPosts(userId) {
      this.posts = [];
      const res = await fetch(
        `https://jsonplaceholder.typicode.com/posts?userId=${userId}`
      );
      const posts = await res.json();
      this.is_data_await = true;
      if (res.ok) {
        this.is_data_await = false;
        this.posts = posts;
      } else {
        console.log("Ошибка получения данных с сервера");
        throw Error;
      }
    },

    /** Получить список пользователей */
    async getUsers() {
      this.users = [];
      const res = await fetch("https://jsonplaceholder.typicode.com/users");
      const users = await res.json();
      this.is_data_await = true;
      if (res.ok) {
        this.is_data_await = false;
        this.users = users;
        this.users_keyBy = _.keyBy(users, "id");
      } else {
        console.log("Ошибка получения данных с сервера");
        throw Error;
      }
    },

    /** Получить список комментариев */
    async getComments() {
      this.comments = [];
      const res = await fetch("https://jsonplaceholder.typicode.com/comments");
      const comments = await res.json();
      if (res.ok) {
        this.comments = comments;
        this.comments_groupBy = _.groupBy(comments, "postId");
      } else {
        console.log("Ошибка получения данных с сервера");
        throw Error;
      }
    },
  },
  modules: {
  }
})
